import React, { useState, useEffect } from "react";
import axios from "axios";
import { Send, Plus, MessageCircle } from "lucide-react"; // Importamos MessageCircle
import "./App.css";

function App() {
  const [conversations, setConversations] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [newConversationTitle, setNewConversationTitle] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchConversations();
  }, []);

  // ... (Funções fetchConversations, createNewConversation, selectConversation, sendMessage permanecem as mesmas)
  // [Atenção: A função sendMessage precisa ser atualizada para usar a nova classe 'bot' na mensagem temporária, veja abaixo]

  const fetchConversations = async () => {
    // ... (código existente)
  };

  const createNewConversation = async () => {
    // ... (código existente)
  };

  const selectConversation = async (conversation) => {
    // ... (código existente)
  };

  // ✅ Função sendMessage atualizada para usar a classe CSS 'bot' no loading
  const sendMessage = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim()) return;
    if (!selectedConversation || !selectedConversation.id) {
      alert("Selecione uma conversa antes de enviar uma mensagem!");
      return;
    }

    const userMessage = inputMessage.trim();
    setInputMessage("");

    // Exibe mensagem do usuário imediatamente
    const userMsg = { user: "Você", content: userMessage };
    setMessages((prev) => [...prev, userMsg]);

    setLoading(true);

    try {
      const res = await axios.post("http://127.0.0.1:8000/chat", {
        message: userMessage,
        conversation_id: selectedConversation.id,
      });

      console.log("Resposta do servidor:", res.data);

      const aiMessage = { user: "Bot", content: res.data.response };
      setMessages((prev) => [...prev, aiMessage]);
    } catch (err) {
      console.error("Erro ao enviar mensagem:", err.response || err);
      // Opcional: Adicionar uma mensagem de erro ao chat
      const errorMessage = { user: "Bot", content: "Ops! Erro ao conectar. Tente novamente." };
      setMessages((prev) => [...prev, errorMessage]);
    } finally {
      setLoading(false);
    }
  };


  // --- NOVO RETURN (Estrutura ajustada com novas classes) ---
  return (
    <div className="container">
      {/* Barra lateral */}
      <div className="sidebar">
        <h2>Conversas</h2>
        <div className="input-group">
          <input
            type="text"
            className="title-input" // Nova classe
            value={newConversationTitle}
            onChange={(e) => setNewConversationTitle(e.target.value)}
            placeholder="Título da conversa"
          />
          <button onClick={createNewConversation} className="add-button"> {/* Nova classe */}
            <Plus size={18} />
          </button>
        </div>

        <div className="conversation-list">
          {conversations.length === 0 && <p>Nenhuma conversa</p>}
          {conversations.map((conv) => (
            <div
              key={conv.id}
              className={`conversation-item ${
                selectedConversation?.id === conv.id ? "selected" : ""
              }`}
              onClick={() => selectConversation(conv)}
            >
              {conv.title}
            </div>
          ))}
        </div>
      </div>

      {/* Área principal de chat */}
      <div className="chat-area">
        <div className="header">
            {selectedConversation ? selectedConversation.title : "Chat Gemini"}
        </div>

        {/* Placeholder para quando não há conversa selecionada */}
        {!selectedConversation && (
            <div className="messages no-conversation">
                <MessageCircle size={64} className="no-conversation-icon" />
                <h3>Bem-vindo ao Chat Gemini!</h3>
                <p>Crie uma nova conversa ou selecione uma existente na barra lateral.</p>
            </div>
        )}

        {/* Mensagens e Formulário de Chat */}
        {selectedConversation && (
            <>
                <div className="messages">
                {messages.map((m, i) => (
                    <div
                    key={i}
                    className={`message ${m.user === "Você" ? "user" : "bot"}`} // 'bot' é o novo 'assistant'
                    >
                        {m.user !== "Você" && <strong>{m.user}: </strong>} {/* Opcional: Remover "Você:" para mensagens do usuário */}
                        {m.content}
                    </div>
                ))}
                {/* Indicador de carregamento */}
                {loading && (
                    <div className="message bot typing-indicator">
                        <div className="dot"></div>
                        <div className="dot"></div>
                        <div className="dot"></div>
                    </div>
                )}
                </div>

                <form onSubmit={sendMessage} className="message-form">
                <input
                    type="text"
                    className="message-input" // Nova classe
                    value={inputMessage}
                    onChange={(e) => setInputMessage(e.target.value)}
                    placeholder="Digite sua mensagem..."
                    disabled={loading}
                />
                <button 
                    type="submit" 
                    className="send-button" // Nova classe
                    disabled={loading || !inputMessage.trim()} // Desabilita se estiver carregando ou sem texto
                >
                    <Send size={20} />
                </button>
                </form>
            </>
        )}
      </div>
    </div>
  );
}

export default App;
