import React, { useState, useEffect } from "react";
import axios from "axios";
import { Send, Plus } from "lucide-react";
import "./App.css";

function App() {
  const [conversations, setConversations] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [newConversationTitle, setNewConversationTitle] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchConversations();
  }, []);

  const fetchConversations = async () => {
    try {
      const res = await axios.get("http://127.0.0.1:8000/conversations");
      console.log("DEBUG: fetchConversations", res.data);
      setConversations(res.data);
    } catch (err) {
      console.error("Erro ao buscar conversas:", err);
    }
  };

  const createNewConversation = async () => {
    const id = "conv-" + Math.random().toString(36).substring(2, 8);
    const title = newConversationTitle.trim() || "Nova Conversa";

    try {
      const res = await axios.post("http://127.0.0.1:8000/conversations", {
        id,
        title
      });
      console.log("DEBUG: createNewConversation", res.data);
      setConversations(prev => [res.data.conversation, ...prev]);
      setSelectedConversation(res.data.conversation);
      setMessages([]);
      setNewConversationTitle("");
    } catch (err) {
      console.error("Erro ao criar conversa:", err.response?.data || err);
    }
  };

  const selectConversation = async (conv) => {
    setSelectedConversation(conv);
    try {
      const res = await axios.get(`http://127.0.0.1:8000/messages/${conv.id}`);
      console.log("DEBUG: fetchMessages", res.data);
      setMessages(res.data);
    } catch (err) {
      console.error("Erro ao buscar mensagens:", err.response?.data || err);
      setMessages([]);
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || !selectedConversation) return;

    const userMessage = inputMessage.trim();
    setInputMessage("");
    setLoading(true);

    setMessages(prev => [...prev, { user: "Você", content: userMessage }]);

    try {
      const res = await axios.post("http://127.0.0.1:8000/chat", {
        conversation_id: selectedConversation.id,
        message: userMessage
      });
      console.log("DEBUG: sendMessage", res.data);
      setMessages(prev => [...prev, { user: "Bot", content: res.data.response }]);
    } catch (err) {
      console.error("Erro ao enviar mensagem:", err.response?.data || err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="sidebar">
        <h2>Conversas</h2>
        <div className="input-group">
          <input
            type="text"
            placeholder="Título da conversa"
            value={newConversationTitle}
            onChange={(e) => setNewConversationTitle(e.target.value)}
          />
          <button onClick={createNewConversation}><Plus size={16} /></button>
        </div>
        <div className="conversation-list">
          {conversations.map(c => (
            <div
              key={c.id}
              className={`conversation-item ${selectedConversation?.id === c.id ? "selected" : ""}`}
              onClick={() => selectConversation(c)}
            >
              {c.title}
            </div>
          ))}
        </div>
      </div>

      <div className="chat-area">
        <h2>Chat Gemini</h2>
        <div className="messages">
          {messages.map((m, i) => (
            <div key={i} className={`message ${m.user === "Você" ? "user" : "bot"}`}>
              <strong>{m.user}: </strong>{m.content}
            </div>
          ))}
          {loading && <div className="message bot">Bot está digitando...</div>}
        </div>
        <form onSubmit={sendMessage} className="message-form">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder={selectedConversation ? "Digite sua mensagem..." : "Selecione uma conversa"}
            disabled={!selectedConversation || loading}
          />
          <button type="submit" disabled={!selectedConversation || loading}><Send size={20} /></button>
        </form>
      </div>
    </div>
  );
}

export default App;
