import React, { useState, useEffect } from "react";
import axios from "axios";
import { Send, Plus, MessageSquare } from "lucide-react";
import "./App.css";

function App() {
  const [conversations, setConversations] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [newConversationTitle, setNewConversationTitle] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchConversations();
  }, []);

  const fetchConversations = async () => {
    try {
      const res = await axios.get("http://127.0.0.1:8000/conversations");
      setConversations(res.data);
      if (res.data.length > 0 && !selectedConversation) {
        setSelectedConversation(res.data[0].conversation_id);
      }
    } catch (err) {
      console.error("Erro ao buscar conversas:", err);
    }
  };

  const fetchMessages = (conversationId) => {
    setSelectedConversation(conversationId);
    const conv = conversation_history[conversationId] || [];
    setMessages(conv);
  };

  const createNewConversation = async () => {
    const title = newConversationTitle.trim() || "Nova Conversa";
    try {
      const res = await axios.post("http://127.0.0.1:8000/conversations", { title });
      setConversations([res.data, ...conversations]);
      setSelectedConversation(res.data.conversation_id);
      setMessages([]);
      setNewConversationTitle("");
    } catch (err) {
      console.error("Erro ao criar conversa:", err);
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || !selectedConversation) return;

    const userMessage = inputMessage.trim();
    setInputMessage("");
    setMessages((prev) => [...prev, { user: "Você", content: userMessage }]);
    setLoading(true);

    try {
      const res = await axios.post("http://127.0.0.1:8000/chat", {
        message: userMessage,
        conversation_id: selectedConversation,
      });
      setMessages((prev) => [...prev, { user: "Bot", content: res.data.response }]);
    } catch (err) {
      console.error("Erro ao enviar mensagem:", err);
      alert("Erro ao enviar mensagem.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="sidebar">
        <h2 className="header">Conversas</h2>
        <div className="input-group">
          <input
            type="text"
            value={newConversationTitle}
            onChange={(e) => setNewConversationTitle(e.target.value)}
            placeholder="Título da conversa"
            className="title-input"
          />
          <button onClick={createNewConversation} className="add-button">
            <Plus size={20} />
          </button>
        </div>
        <div className="conversation-list">
          {conversations.map((c) => (
            <div
              key={c.conversation_id}
              className={`conversation-item ${selectedConversation === c.conversation_id ? "selected" : ""}`}
              onClick={() => fetchMessages(c.conversation_id)}
              title={c.title}
            >
              {c.title}
            </div>
          ))}
        </div>
      </div>

      <div className="chat-area">
        <h2 className="header">Chat IA (Gemini)</h2>
        <div className="messages">
          {messages.map((m, i) => (
            <div key={i} className={`message ${m.user === "Você" ? "user" : "assistant"}`}>
              {m.content}
            </div>
          ))}
          {loading && <div className="message assistant"><em>IA está digitando...</em></div>}
        </div>
        <form onSubmit={sendMessage} className="message-form">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder={selectedConversation ? "Digite sua mensagem..." : "Selecione uma conversa"}
            disabled={!selectedConversation || loading}
            className="message-input"
          />
          <button type="submit" disabled={!inputMessage.trim() || !selectedConversation || loading} className="send-button">
            <Send size={24} color="white" />
          </button>
        </form>
      </div>
    </div>
  );
}

export default App;
