import React, { useState, useEffect } from "react";
import axios from "axios";
import { Send, Plus, MessageSquare } from "lucide-react";
import "./App.css";

function App() {
  const userId = "global-user-system";
  const [conversations, setConversations] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const [newConversationTitle, setNewConversationTitle] = useState("");

  useEffect(() => {
    fetchConversations();
  }, []);

  // BUSCAR CONVERSAS
  const fetchConversations = async () => {
    try {
      const res = await axios.get(`http://127.0.0.1:8000/conversations/${userId}`);
      setConversations(res.data);
      if (res.data.length > 0 && !selectedConversation) {
        const firstConv = res.data[0].conversation_id;
        fetchMessages(firstConv);
      }
    } catch (err) {
      console.error("Erro ao buscar conversas:", err);
    }
  };

  // BUSCAR MENSAGENS
  const fetchMessages = async (conversationId) => {
    setSelectedConversation(conversationId);
    try {
      const res = await axios.get(`http://127.0.0.1:8000/messages/${conversationId}`);
      setMessages(res.data);
    } catch (err) {
      console.error("Erro ao buscar mensagens:", err);
    }
  };

  // CRIAR NOVA CONVERSA
  const createNewConversation = () => {
    const title = newConversationTitle.trim() || "Nova Conversa";
    const newId = "conv-" + Math.random().toString(36).substring(2, 8);
    const newConv = { conversation_id: newId, title, last_message: "" };
    setConversations((prev) => [newConv, ...prev]);
    setSelectedConversation(newId);
    setMessages([]);
    setNewConversationTitle("");
  };

  // ENVIAR MENSAGEM
  const sendMessage = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || !selectedConversation || loading) return;

    const userMessageContent = inputMessage.trim();
    setInputMessage("");

    const userMessageTemp = {
      id: Date.now(),
      user: userId,
      content: userMessageContent,
      conversation_id: selectedConversation,
    };
    setMessages((prev) => [...prev, userMessageTemp]);

    try {
      setLoading(true);
      // Envia para backend
      await axios.post("http://127.0.0.1:8000/message", {
        user_id: userId,
        conversation_id: selectedConversation,
        message: userMessageContent,
      });

      const aiRes = await axios.post("http://127.0.0.1:8000/chat", {
        user_id: userId,
        conversation_id: selectedConversation,
        message: userMessageContent,
      });

      const aiMessage = {
        id: Date.now() + 1,
        user: "assistant",
        content: aiRes.data.reply,
        conversation_id: selectedConversation,
      };

      setMessages((prev) => [...prev, aiMessage]);
      fetchConversations();
    } catch (err) {
      console.error("Erro ao enviar mensagem:", err);
      alert("Erro ao enviar mensagem. Verifique o console.");
    } finally {
      setLoading(false);
    }
  };

  const renderChatContent = () => {
    if (!selectedConversation) {
      return (
        <div className="no-conversation">
          <MessageSquare size={60} className="no-conversation-icon" />
          <p>
            <strong>Nenhuma Conversa Selecionada</strong>
            <br />
            Selecione ou crie uma nova conversa para começar a interagir.
          </p>
        </div>
      );
    }

    return (
      <div style={{ display: "flex", flexDirection: "column", height: "100%" }}>
        <div style={{ flexGrow: 1, overflowY: "auto", marginBottom: "15px", padding: "10px 0" }}>
          {messages.map((m, index) => (
            <div
              key={`${m.conversation_id}-${m.id}-${index}`}
              style={{
                marginBottom: "10px",
                display: "flex",
                justifyContent: m.user === userId ? "flex-end" : "flex-start",
              }}
            >
              <div className={`message ${m.user === userId ? "user" : "assistant"}`}>
                {m.content}
              </div>
            </div>
          ))}

          {loading && (
            <div style={{ display: "flex", justifyContent: "flex-start", marginBottom: "10px" }}>
              <div className="message assistant">
                <em>IA está digitando...</em>
              </div>
            </div>
          )}
        </div>
      </div>
    );
  };

  return (
    <div className="container">
      <div className="sidebar">
        <h2 className="header">Conversas</h2>
        <div className="input-group">
          <input
            type="text"
            value={newConversationTitle}
            onChange={(e) => setNewConversationTitle(e.target.value)}
            placeholder="Título da conversa"
            className="title-input"
          />
          <button
            onClick={createNewConversation}
            className="add-button"
            aria-label="Criar nova conversa"
          >
            <Plus size={20} />
          </button>
        </div>
        <div className="conversation-list">
          {conversations.length > 0 ? (
            conversations.map((c) => (
              <div
                key={c.conversation_id}
                className={`conversation-item ${
                  selectedConversation === c.conversation_id ? "selected" : ""
                }`}
                onClick={() => fetchMessages(c.conversation_id)}
                title={c.title || c.conversation_id}
              >
                {c.title || c.conversation_id}
              </div>
            ))
          ) : (
            <p style={{ color: "#888", fontSize: "14px" }}>Nenhuma conversa encontrada.</p>
          )}
        </div>
      </div>

      <div className="chat-area">
        <h2 className="header">
          Chat IA (<span style={{ color: "#007bff" }}>LangChain + OpenAi</span>)
        </h2>
        <div style={{ flexGrow: 1, display: "flex", flexDirection: "column" }}>
          {renderChatContent()}
        </div>
        <form onSubmit={sendMessage} className="message-form">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder={
              selectedConversation
                ? "Digite sua mensagem..."
                : "Selecione uma conversa para digitar"
            }
            className="message-input"
            disabled={!selectedConversation || loading}
          />
          <button
            type="submit"
            className="send-button"
            disabled={!selectedConversation || loading || !inputMessage.trim()}
          >
            <Send size={24} color="white" />
          </button>
        </form>
      </div>
    </div>
  );
}

export default App;
