import React, { useState, useEffect } from "react";
import axios from "axios";
import { Send, Plus, MessageCircle } from "lucide-react"; // Adicionado MessageCircle
import "./App.css";

function App() {
  const [conversations, setConversations] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [newConversationTitle, setNewConversationTitle] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchConversations();
  }, []);

  // ‚úÖ Buscar todas as conversas
  const fetchConversations = async () => {
    try {
      const res = await axios.get("http://127.0.0.1:8000/conversations");
      console.log("Conversas carregadas:", res.data);
      setConversations(res.data);

      if (res.data.length > 0) {
        // Seleciona automaticamente a primeira conversa existente
        selectConversation(res.data[0]);
      } else {
        setSelectedConversation(null);
        setMessages([]);
      }
    } catch (err) {
      console.error("Erro ao buscar conversas:", err);
    }
  };

  // ‚úÖ Criar nova conversa
  const createNewConversation = async () => {
    const title = newConversationTitle.trim() || "Nova Conversa";
    try {
      const res = await axios.post("http://127.0.0.1:8000/conversations", { title });
      console.log("Nova conversa criada:", res.data);
      setConversations((prev) => [res.data, ...prev]);
      setNewConversationTitle("");
      await selectConversation(res.data);
    } catch (err) {
      console.error("Erro ao criar conversa:", err.response || err);
      alert("Erro ao criar conversa. Verifique o console para detalhes.");
    }
  };

  // ‚úÖ Selecionar conversa e carregar mensagens
  const selectConversation = async (conversation) => {
    if (!conversation || !conversation.id) {
      console.warn("Nenhuma conversa v√°lida selecionada:", conversation);
      setSelectedConversation(null);
      setMessages([]);
      return;
    }

    setSelectedConversation(conversation);
    console.log("Selecionando conversa:", conversation);

    try {
      const res = await axios.get(`http://127.0.0.1:8000/messages/${conversation.id}`);
      console.log("Mensagens carregadas:", res.data);
      setMessages(res.data);
    } catch (err) {
      console.error("Erro ao buscar mensagens:", err.response || err);
      setMessages([]);
    }
  };

  // ‚úÖ Enviar mensagem
  const sendMessage = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim()) return;
    if (!selectedConversation || !selectedConversation.id) {
      alert("Selecione uma conversa antes de enviar uma mensagem!");
      return;
    }

    const userMessage = inputMessage.trim();
    setInputMessage("");

    // Exibe mensagem do usu√°rio imediatamente
    const userMsg = { user: "Voc√™", content: userMessage };
    setMessages((prev) => [...prev, userMsg]);

    setLoading(true);

    try {
      const res = await axios.post("http://127.0.0.1:8000/chat", {
        message: userMessage,
        conversation_id: selectedConversation.id,
      });

      console.log("Resposta do servidor:", res.data);

      const aiMessage = { user: "Bot", content: res.data.response };
      setMessages((prev) => [...prev, aiMessage]);
    } catch (err) {
      console.error("Erro ao enviar mensagem:", err.response || err);
      // Mensagem de erro sutil no chat
      const errorMessage = { user: "Bot", content: "‚ùå Erro ao enviar. Tente novamente." };
      setMessages((prev) => [...prev, errorMessage]);
    } finally {
      setLoading(false);
    }
  };

  return (
    // üí° NOVO CONTAINER PRINCIPAL: app-container para centraliza√ß√£o e sombra
    <div className="app-container">
      {/* Barra lateral */}
      <div className="sidebar">
        <h2>Conversas</h2>
        <div className="input-group">
          <input
            type="text"
            className="title-input" // Classe para estiliza√ß√£o de input no CSS
            value={newConversationTitle}
            onChange={(e) => setNewConversationTitle(e.target.value)}
            placeholder="T√≠tulo da conversa"
          />
          <button onClick={createNewConversation} className="add-button"> {/* Classe para bot√£o no CSS */}
            <Plus size={18} />
          </button>
        </div>

        <div className="conversation-list">
          {conversations.length === 0 && <p>Nenhuma conversa</p>}
          {conversations.map((conv) => (
            <div
              key={conv.id}
              className={`conversation-item ${
                selectedConversation?.id === conv.id ? "selected" : ""
              }`}
              onClick={() => selectConversation(conv)}
            >
              {conv.title}
            </div>
          ))}
        </div>
      </div>

      {/* √Årea principal de chat */}
      <div className="chat-area">
        {/* Cabe√ßalho de Chat */}
        <div className="header">
            {selectedConversation ? selectedConversation.title : "Chat Gemini"}
        </div>

        {/* Placeholder para In√≠cio */}
        {!selectedConversation && (
            <div className="messages no-conversation">
                <MessageCircle size={64} style={{ marginBottom: '20px', color: '#dadce0' }} />
                <h3>Bem-vindo ao Chat Gemini!</h3>
                <p>Crie uma nova conversa ou selecione uma existente na barra lateral para come√ßar a interagir.</p>
            </div>
        )}

        {/* Mensagens e Formul√°rio de Chat (aparecem apenas se houver conversa selecionada) */}
        {selectedConversation && (
            <>
                {/* Container de mensagens */}
                <div className="messages">
                {messages.map((m, i) => (
                    <div
                    key={i}
                    className={`message ${m.user === "Voc√™" ? "user" : "bot"}`}
                    >
                        {/* Remove o r√≥tulo "Voc√™:" da mensagem do usu√°rio para um visual mais limpo */}
                        {m.user !== "Voc√™" && <strong>{m.user}: </strong>}
                        {m.content}
                    </div>
                ))}
                {/* Indicador de carregamento aprimorado */}
                {loading && (
                    <div className="message bot typing-indicator">
                        <div className="dot"></div>
                        <div className="dot"></div>
                        <div className="dot"></div>
                    </div>
                )}
                </div>

                {/* Formul√°rio de Input */}
                <form onSubmit={sendMessage} className="message-form">
                <input
                    type="text"
                    className="message-input" // Classe para input de mensagem
                    value={inputMessage}
                    onChange={(e) => setInputMessage(e.target.value)}
                    placeholder="Digite sua mensagem..."
                    disabled={loading}
                />
                <button 
                    type="submit" 
                    className="send-button" // Classe para bot√£o de envio
                    disabled={loading || !inputMessage.trim()}
                >
                    <Send size={20} />
                </button>
                </form>
            </>
        )}
      </div>
    </div>
  );
}

export default App;
