import React, { useState } from "react";
import axios from "axios";
import { Send, Plus } from "lucide-react";
import "./App.css";

function App() {
  const [conversations, setConversations] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [newConversationTitle, setNewConversationTitle] = useState("");
  const [loading, setLoading] = useState(false);

  // Criar nova conversa
  const createNewConversation = () => {
    const title = newConversationTitle.trim() || "Nova Conversa";
    const newId = "conv-" + Math.random().toString(36).substring(2, 8);
    const newConv = { id: newId, title, messages: [] };
    console.log("Criando conversa:", newConv);
    setConversations((prev) => [newConv, ...prev]);
    setSelectedConversation(newConv);
    setMessages([]);
    setNewConversationTitle("");
  };

  // Selecionar conversa
  const selectConversation = (conv) => {
    console.log("Selecionando conversa:", conv);
    setSelectedConversation(conv);
    setMessages(conv.messages || []);
  };

  // Enviar mensagem
const sendMessage = async (e) => {
  e.preventDefault();
  if (!inputMessage.trim() || !selectedConversation) return;

  const userMessage = inputMessage.trim();
  console.log("Mensagem do usuário:", userMessage);

  setInputMessage("");
  setLoading(true);

  const tempMessage = { user: "Você", content: userMessage };
  setMessages((prev) => [...prev, tempMessage]);

  try {
    console.log("Enviando requisição para o backend...");
    const res = await axios.post("http://127.0.0.1:8000/chat", {
      message: userMessage,
      conversation_id: selectedConversation.id
    });

    console.log("Resposta do backend:", res.data);

    const aiMessage = { user: "Bot", content: res.data.response };
    setMessages((prev) => [...prev, aiMessage]);

    setConversations((prev) =>
      prev.map((c) =>
        c.id === selectedConversation.id
          ? { ...c, messages: [...messages, tempMessage, aiMessage] }
          : c
      )
    );
  } catch (err) {
    console.error("Erro ao enviar mensagem:", err);
    if (err.response) {
      console.error("Detalhes do erro do servidor:", err.response.data);
    } else if (err.request) {
      console.error("Erro na requisição:", err.request);
    } else {
      console.error("Erro desconhecido:", err.message);
    }
    alert("Erro ao enviar mensagem. Veja o console para detalhes.");
  } finally {
    setLoading(false);
  }
};


  return (
    <div className="container">
      <div className="sidebar">
        <h2>Conversas</h2>
        <div className="input-group">
          <input
            type="text"
            value={newConversationTitle}
            onChange={(e) => setNewConversationTitle(e.target.value)}
            placeholder="Título da conversa"
          />
          <button onClick={createNewConversation}>
            <Plus size={16} />
          </button>
        </div>
        <div className="conversation-list">
          {conversations.length === 0 && <p>Nenhuma conversa</p>}
          {conversations.map((conv) => (
            <div
              key={conv.id}
              className={`conversation-item ${selectedConversation?.id === conv.id ? "selected" : ""}`}
              onClick={() => selectConversation(conv)}
            >
              {conv.title}
            </div>
          ))}
        </div>
      </div>

      <div className="chat-area">
        <h2>Chat Gemini - Debug Mode</h2>
        <div className="messages">
          {messages.map((m, i) => (
            <div key={i} className={`message ${m.user === "Você" ? "user" : "bot"}`}>
              <strong>{m.user}: </strong>
              {m.content}
            </div>
          ))}
          {loading && <div className="message bot">Bot está digitando...</div>}
        </div>
        <form onSubmit={sendMessage} className="message-form">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder={selectedConversation ? "Digite sua mensagem..." : "Selecione uma conversa"}
            disabled={!selectedConversation || loading}
          />
          <button type="submit" disabled={!selectedConversation || loading}>
            <Send size={20} />
          </button>
        </form>
      </div>
    </div>
  );
}

export default App;
