import React, { useState, useEffect } from "react";
import axios from "axios";
import { Send, Plus, MessageSquare } from "lucide-react";
import "./App.css";

function App() {
  const [conversations, setConversations] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [newConversationTitle, setNewConversationTitle] = useState("");
  const [loading, setLoading] = useState(false);

  // Buscar todas as conversas ao carregar
  useEffect(() => {
    fetchConversations();
  }, []);

  const fetchConversations = async () => {
    try {
      console.log("Buscando conversas...");
      const res = await axios.get("http://127.0.0.1:8000/conversations");
      console.log("Conversas recebidas:", res.data);
      setConversations(res.data);

      // Seleciona a primeira conversa automaticamente
      if (res.data.length > 0 && !selectedConversation) {
        selectConversation(res.data[0]);
      }
    } catch (err) {
      console.error("Erro ao buscar conversas:", err);
    }
  };

  // Selecionar conversa e buscar mensagens
  const selectConversation = async (conv) => {
    if (!conv) return;
    setSelectedConversation(conv);
    console.log("Selecionada conversa:", conv);

    try {
      const res = await axios.get(`http://127.0.0.1:8000/messages/${conv.id}`);
      console.log("Mensagens recebidas:", res.data);
      setMessages(res.data);
    } catch (err) {
      console.error("Erro ao buscar mensagens:", err);
      setMessages([]);
    }
  };

  // Criar nova conversa
  const createNewConversation = async () => {
    if (!newConversationTitle.trim()) return;

    try {
      console.log("Criando nova conversa:", newConversationTitle);
      const res = await axios.post("http://127.0.0.1:8000/conversations", {
        title: newConversationTitle.trim(),
      });

      console.log("Conversa criada:", res.data);
      const newConv = res.data;
      setConversations((prev) => [newConv, ...prev]);
      setNewConversationTitle("");
      selectConversation(newConv);
    } catch (err) {
      console.error("Erro ao criar conversa:", err);
    }
  };

  // Enviar mensagem
  const sendMessage = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || !selectedConversation) return;

    const userMessage = inputMessage.trim();
    setInputMessage("");
    setLoading(true);

    // Mensagem temporária local
    const tempMessage = { user: "Você", content: userMessage };
    setMessages((prev) => [...prev, tempMessage]);

    try {
      console.log("Enviando mensagem para conversa:", selectedConversation.id);
      const res = await axios.post("http://127.0.0.1:8000/chat", {
        conversation_id: selectedConversation.id,
        message: userMessage,
      });

      console.log("Resposta recebida do backend:", res.data);
      const aiMessage = { user: "Bot", content: res.data.response };
      setMessages((prev) => [...prev, aiMessage]);

      // Atualiza a conversa selecionada no estado
      setConversations((prev) =>
        prev.map((c) =>
          c.id === selectedConversation.id
            ? { ...c, messages: [...prev.messages, tempMessage, aiMessage] }
            : c
        )
      );
    } catch (err) {
      console.error("Erro ao enviar mensagem:", err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="sidebar">
        <h2>Conversas</h2>
        <div className="input-group">
          <input
            type="text"
            value={newConversationTitle}
            onChange={(e) => setNewConversationTitle(e.target.value)}
            placeholder="Título da conversa"
          />
          <button onClick={createNewConversation}>
            <Plus size={16} />
          </button>
        </div>

        <div className="conversation-list">
          {conversations.length === 0 && <p>Nenhuma conversa</p>}
          {conversations.map((conv) => (
            <div
              key={conv.id}
              className={`conversation-item ${
                selectedConversation?.id === conv.id ? "selected" : ""
              }`}
              onClick={() => selectConversation(conv)}
            >
              {conv.title}
            </div>
          ))}
        </div>
      </div>

      <div className="chat-area">
        <h2>Chat Gemini</h2>
        <div className="messages">
          {messages.map((m, i) => (
            <div
              key={i}
              className={`message ${m.user === "Você" ? "user" : "bot"}`}
            >
              <strong>{m.user}: </strong>
              {m.content}
            </div>
          ))}
          {loading && <div className="message bot">Bot está digitando...</div>}
        </div>

        <form onSubmit={sendMessage} className="message-form">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder={
              selectedConversation
                ? "Digite sua mensagem..."
                : "Selecione uma conversa"
            }
            disabled={!selectedConversation || loading}
          />
          <button type="submit" disabled={!selectedConversation || loading}>
            <Send size={20} />
          </button>
        </form>
      </div>
    </div>
  );
}

export default App;
