import React, { useState } from "react";
import axios from "axios";

function App() {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState([]);

  const sendMessage = async () => {
    if (!input.trim()) return;
    try {
      setMessages([...messages, { role: "user", content: input }]);
      const response = await axios.post("http://127.0.0.1:8000/message", {
        message: input
      });
      const aiMessage = response.data.response;
      setMessages(prev => [...prev, { role: "ai", content: aiMessage }]);
      setInput("");
    } catch (error) {
      console.error("Erro ao enviar mensagem:", error);
      alert("Erro ao conectar com o backend. Verifique se o servidor está rodando.");
    }
  };

  return (
    <div style={{ padding: 20, fontFamily: "Arial, sans-serif" }}>
      <h1>Chat Bot com Memória</h1>
      <div style={{ border: "1px solid #ccc", padding: 10, maxHeight: "400px", overflowY: "auto", marginBottom: 10 }}>
        {messages.map((msg, index) => (
          <p key={index} style={{ margin: "5px 0" }}>
            <b>{msg.role === "user" ? "Você" : "IA"}:</b> {msg.content}
          </p>
        ))}
      </div>
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Digite sua mensagem..."
        style={{ width: "70%", padding: 5 }}
      />
      <button onClick={sendMessage} style={{ padding: 5, marginLeft: 5 }}>
        Enviar
      </button>
    </div>
  );
}

export default App;
